commits.content:
.header {
	display: flex;
	flex-direction: column;
	align-items: center;
	width: 100%;
	z-index: 10;

	&__home {
		position: fixed;
		display: flex;
		justify-content: center;
		top: 0;
		width: 100%;
		max-width: 1200px;
		font-size: $fontsize-xl;
		background-color: $color-bgc;
	}

	&__logo {
		display: block;
		height: 1em;
		padding: .2em .5em 0 0;
	}

	&__title {
		font-family: 'Unkempt', cursive;
		font-size: 1em;
		text-align: center;
		color: $color-header-text;
	}

}

.header::after {
	content: "";
	height: 60vmin;
	width: 100%;
	max-width: 1200px;
	background-image: url('../img/setup_1280_400.jpg');
	background-size: cover;
	background-position: 50%;
}
